import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useLocation } from 'react-router-dom';
import Kanban from '../components/Kanban/Kanban';
import './App.scss'

const App = () => {
  const location = useLocation();

  const getNavBarClass = () => {
    switch (location.pathname) {
      case "/":
        return "home-nav-bar";
      case "/my-component":
        return "kanban-nav-bar";
      default:
        return "default-nav-bar";
    }
  };
  return (
    <>
      <nav className='nav-bar'>
        <Link to="/" className={`${location.pathname=='/'?'home-nav-bar':'default-nav-bar'}`}>Home</Link>  |   
        <Link to="/my-component" className={`${location.pathname=='/my-component'?'kanban-nav-bar':'default-nav-bar'}`}>Kanban Board</Link>
      </nav>
      <Routes>
        <Route path="/" element={<h1>Welcome to the Home Page</h1>} />
        <Route path="/my-component" element={<Kanban />} />
      </Routes>
    </>
  );
};


export default App;
